using System.Collections.Generic;using System.Threading.Tasks;using SQLite;using MusicCalculator.Models;namespace MusicCalculator.Data{    public class SongDatabase    {        readonly SQLiteAsyncConnection _database;        public SongDatabase(string dbPath)        {            _database = new SQLiteAsyncConnection(dbPath);            _database.CreateTableAsync<Song>().Wait();        }        public Task<List<Song>> GetSongsAsync()        {            return _database.Table<Song>().ToListAsync();        }        public Task<Song> GetSongAsync(int id)        {            return _database.Table<Song>()                            .Where(i => i.ID == id)                            .FirstOrDefaultAsync();        }        public Task<int> SaveSongAsync(Song song)        {            if (song.ID != 0)            {                return _database.UpdateAsync(song);            }            else            {                return _database.InsertAsync(song);            }        }        public Task<int> DeleteSongAsync(Song song)        {            return _database.DeleteAsync(song);        }    }}